import React from 'react';
import WidgetCard from '../components/Cards/WidgetCard';
import LineChart from '../components/charts/LineChart';
import BarChart from '../components/charts/BarChart';
import PieChart from '../components/charts/PieChart';
import AreaChart from '../components/charts/AreaChart';
import '../styles/Dashboard.css';

const Dashboard = () => {
  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Admin Dashboard</h1>

      <div className="widgets-section">
        <WidgetCard label="Total Users" value="1,200" />
        <WidgetCard label="Sales Revenue" value="$23,000" />
        <WidgetCard label="Total Visits" value="11,000" />
        <WidgetCard label="Active Users" value="450" />
      </div>

      <div className="charts-section">
        <div className="chart-card"><LineChart /></div>
        <div className="chart-card"><BarChart /></div>
        <div className="chart-card"><PieChart /></div>
        <div className="chart-card"><AreaChart /></div>
      </div>

      <div className="stats-section">
        <div className="stats-card">
          <h3>New Users</h3>
          <p>320 this week</p>
        </div>
        <div className="stats-card">
          <h3>Pending Orders</h3>
          <p>56 orders</p>
        </div>
        <div className="stats-card">
          <h3>Revenue Growth</h3>
          <p>+18% compared to last month</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
