import React, { useState } from "react";
import WidgetCard from "../components/Cards/WidgetCard";

import Sidebar from '../components/Sidebar';
import LineChart from "../components/charts/LineChart";
import BarChart from "../components/charts/BarChart";
import PieChart from "../components/charts/PieChart";
import AreaChart from "../components/charts/AreaChart";
import { FiDollarSign, FiUsers, FiBox, FiTrendingUp, FiRepeat } from "react-icons/fi";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <div className="dashboard-layout">
      <Sidebar collapsed={collapsed} setCollapsed={setCollapsed} />

      <div className={`dashboard-container ${collapsed ? "collapsed" : ""}`}>
        <h1 className="dashboard-title">Admin Dashboard</h1>

        {/* ... rest of your dashboard content ... */}
        <div className="scroll-container">
          <WidgetCard icon={<FiDollarSign />} label="Earnings" value="$63,448.78" buttonText="Download" />
          <WidgetCard icon={<FiUsers />} label="Customers" value="39,354" percentage="-4%" isNegative />
          <WidgetCard icon={<FiBox />} label="Products" value="4,396" percentage="+23%" />
          <WidgetCard icon={<FiTrendingUp />} label="Sales" value="423,39" percentage="+38%" />
          <WidgetCard icon={<FiRepeat />} label="Refunds" value="39,354" percentage="-12%" isNegative />
        </div>

        {/* (Other sections unchanged) */}
      </div>
    </div>
  );
};

export default Dashboard;
